import {
    useState,
    useCallback,
  } from 'react';

  function func(url) {
    return fetch(url)  // return this promise
    .then(response => response.json())
    .then(json => (json.results))
}

  export const getTodos = async () => {
    console.log('123');
    const response = await func('http://dummy.restapiexample.com/api/v1/employees');
    console.log('response', response.body);
    const data = await response.json().length;
    console.log('data', data);
    // add transformers here if needed

    return data;
  };

  export const useGetTodos = () => {
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState(null);
    const [data, setData] = useState(0);

    const execute = async () => {
      try {
        setIsLoading(true);
        const todos = await getTodos();
        setData(todos);
        return todos;
      } catch (e) {
        setError(e);
        setIsLoading(false);
        throw e;
      }
    };

    return {
      isLoading,
      error,
      data,
      execute: useCallback(execute, []), // to avoid infinite calls when inside a `useEffect`
    };
  };
